import React, { useState, useEffect } from "react";
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";
import { Input } from "./components/input";
import { Button } from "./components/button";

interface Repo {
  id: number;
  name: string;
  html_url: string;
}

interface CommitData {
  month: string;
  commits: number;
}

export default function GitHubActivityViewer() {
  const [username, setUsername] = useState<string>("");
  const [repos, setRepos] = useState<Repo[]>([]);
  const [commitData, setCommitData] = useState<CommitData[]>([]);
  const [loading, setLoading] = useState<boolean>(false);

  const fetchRepos = async () => {
    if (!username) return;
    setLoading(true);
    const res = await fetch(`https://api.github.com/users/${username}/repos?per_page=100`);
    const data = await res.json();
    setRepos(data);
    setLoading(false);
  };

  const fetchCommits = async () => {
    const currentYear = new Date().getFullYear();
    const monthlyCommits: CommitData[] = Array.from({ length: 12 }, (_, i) => ({
      month: new Date(currentYear, i).toLocaleString("default", { month: "short" }),
      commits: 0,
    }));

    for (const repo of repos) {
      for (let i = 0; i < 12; i++) {
        const since = new Date(currentYear, i, 1).toISOString();
        const until = new Date(currentYear, i + 1, 1).toISOString();

        const res = await fetch(`https://api.github.com/repos/${username}/${repo.name}/commits?since=${since}&until=${until}`);
        if (res.status === 200) {
          const commits = await res.json();
          monthlyCommits[i].commits += commits.length;
        }
      }
    }
    setCommitData(monthlyCommits);
  };

  const handleSearch = async () => {
    await fetchRepos();
  };

  useEffect(() => {
    if (repos.length > 0) fetchCommits();
  }, [repos]);

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-4 text-center">GitHub Activity Viewer</h1>
      <div className="flex gap-2 mb-6 justify-center">
        <Input
          placeholder="Enter GitHub username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="w-full max-w-md"
        />
        <Button onClick={handleSearch}>Search</Button>
      </div>

      {loading && <p className="text-center">Loading repositories...</p>}

      {repos.length > 0 && (
        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-2">Repositories</h2>
          <ul className="list-disc ml-6 space-y-1">
            {repos.map((repo) => (
              <li key={repo.id}>
                <a href={repo.html_url} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">
                  {repo.name}
                </a>
              </li>
            ))}
          </ul>
        </div>
      )}

      {commitData.length > 0 && (
        <div>
          <h2 className="text-2xl font-semibold mb-2">Commits in {new Date().getFullYear()}</h2>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={commitData}>
              <XAxis dataKey="month" />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Bar dataKey="commits" fill="#4f46e5" radius={[4, 4, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}
