import {
    ResponsiveContainer,
    LineChart,
    BarChart,
    Bar,
    Line,
    XAxis,
    YAxis,
    Tooltip,
    CartesianGrid,
  } from "recharts";
  import { useState, useMemo } from "react";
  import { Button } from "@/components/ui/button";
  
  interface CommitData {
    date: string; // Format: YYYY-MM-DD
    count: number;
  }
  
  interface CommitChartProps {
    data: CommitData[];
  }
  
  const CommitChart: React.FC<CommitChartProps> = ({ data }) => {
    const [view, setView] = useState<"daily" | "monthly">("daily");
  
    const toggleView = () => {
      setView((prev) => (prev === "daily" ? "monthly" : "daily"));
    };
  
    const monthlyData = useMemo(() => {
      const monthMap: Record<string, number> = {};
  
      data.forEach(({ date, count }) => {
        const month = date.slice(0, 7); // 'YYYY-MM'
        monthMap[month] = (monthMap[month] || 0) + count;
      });
  
      return Object.entries(monthMap).map(([month, count]) => ({
        date: month,
        count,
      }));
    }, [data]);
  
    const dailyData = useMemo(() => {
      return data.sort((a, b) => a.date.localeCompare(b.date));
    }, [data]);
  
    return (
      <div className="mt-6">
        <div className="flex items-center justify-between mb-2">
          <h2 className="text-lg font-semibold">
            {view === "daily" ? "Commits per Day (Year)" : "Commits per Month"}
          </h2>
          <Button variant="outline" onClick={toggleView}>
            Switch to {view === "daily" ? "Monthly" : "Daily"} View
          </Button>
        </div>
  
        <ResponsiveContainer width="100%" height={300}>
          {view === "daily" ? (
            <LineChart data={dailyData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Line
                type="monotone"
                dataKey="count"
                stroke="#4f46e5"
                strokeWidth={2}
              />
            </LineChart>
          ) : (
            <BarChart data={monthlyData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Bar dataKey="count" fill="#4f46e5" />
            </BarChart>
          )}
        </ResponsiveContainer>
      </div>
    );
  };
  
  export default CommitChart;
