import {
    ResponsiveContainer,
    BarChart,
    Bar,
    XAxis,
    YAxis,
    Tooltip,
    CartesianGrid,
  } from "recharts";
  import { useState, useMemo, useEffect } from "react";
  import { Label } from "./ui/label";
  import {
    Select,
    SelectTrigger,
    SelectValue,
    SelectContent,
    SelectItem,
  } from "./ui/select";
  
  interface CommitData {
    date: string; // Format: YYYY-MM-DD
    count: number;
  }
  
  interface CommitChartProps {
    data: CommitData[];
  }
  
  const CommitChart: React.FC<CommitChartProps> = ({ data }) => {
    const years = useMemo(() => {
      const yearSet = new Set<string>();
      data.forEach(({ date }) => {
        const year = date.slice(0, 4);
        yearSet.add(year);
      });
      return Array.from(yearSet).sort((a, b) => parseInt(a) - parseInt(b));
    }, [data]);
  
    const [selectedYear, setSelectedYear] = useState<string>("");
  
    // Automatically set latest year once available
    useEffect(() => {
      if (years.length > 0 && !selectedYear) {
        setSelectedYear(years[years.length - 1]);
      }
    }, [years, selectedYear]);
  
    const monthlyData = useMemo(() => {
      if (!selectedYear) return [];
  
      const monthMap: Record<string, number> = {};
  
      data.forEach(({ date, count }) => {
        if (date.startsWith(selectedYear)) {
          const monthKey = date.slice(0, 7); // YYYY-MM
          monthMap[monthKey] = (monthMap[monthKey] || 0) + count;
        }
      });
  
      return Array.from({ length: 12 }, (_, i) => {
        const monthLabel = String(i + 1); // 1–12
        const monthKey = `${selectedYear}-${String(i + 1).padStart(2, "0")}`;
        return {
          date: monthLabel,
          count: monthMap[monthKey] || 0,
        };
      });
    }, [data, selectedYear]);
  
    return (
      <div className="mt-6 space-y-4">
        <div className="flex items-center justify-between gap-2">
          <h2 className="text-lg font-semibold">Monthly Commits</h2>
          <div className="flex gap-2 items-center">
            <Label>Select Year:</Label>
            <Select value={selectedYear} onValueChange={setSelectedYear}>
              <SelectTrigger className="w-[120px]">
                <SelectValue placeholder="Select year" />
              </SelectTrigger>
              <SelectContent>
                {years.map((year) => (
                  <SelectItem key={year} value={year}>
                    {year}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </div>
  
        <ResponsiveContainer width="100%" height={350}>
          <BarChart data={monthlyData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" label={{ value: "Month", position: "insideBottom", offset: -5 }} />
            <YAxis allowDecimals={false} label={{ value: "Commits", angle: -90, position: "insideLeft" }} />
            <Tooltip />
            <Bar dataKey="count" fill="#4f46e5" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    );
  };
  
  export default CommitChart;
