import React, { useState, useEffect } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { Input } from "./components/input";
import { Button } from "./components/button";

interface Repo {
  id: number;
  name: string;
  html_url: string;
}

interface CommitData {
  month: string;
  commits: number;
}

const GITHUB_TOKEN = import.meta.env.VITE_GITHUB_TOKEN;

export default function GitHubActivityViewer() {
  const [username, setUsername] = useState<string>("");
  const [repos, setRepos] = useState<Repo[]>([]);
  const [commitData, setCommitData] = useState<CommitData[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [years, setYears] = useState<number[]>([]);
  const [selectedYear, setSelectedYear] = useState<number | null>(null);
  const [showChart, setShowChart] = useState(false);

  const fetchWithAuth = async (url: string) => {
    return fetch(url, {
      headers: {
        Authorization: `token ${GITHUB_TOKEN}`,
      },
    });
  };

  const fetchReposAndYears = async () => {
    if (!username) return;
    setLoading(true);
    setCommitData([]);
    setShowChart(false);

    const userRes = await fetchWithAuth(`https://api.github.com/users/${username}`);
    const userData = await userRes.json();

    const createdYear = new Date(userData.created_at).getFullYear();
    const currentYear = new Date().getFullYear();
    const yearRange = Array.from({ length: currentYear - createdYear + 1 }, (_, i) => createdYear + i);
    setYears(yearRange.reverse()); // latest first
    setSelectedYear(currentYear);

    const reposRes = await fetchWithAuth(`https://api.github.com/users/${username}/repos?per_page=100`);
    const reposData = await reposRes.json();
    setRepos(reposData);
    setLoading(false);
  };

  const fetchCommits = async (year: number) => {
    if (repos.length === 0) return;
    setLoading(true);
    setCommitData([]);

    const monthlyCommits: CommitData[] = Array.from({ length: 12 }, (_, i) => ({
      month: new Date(year, i).toLocaleString("default", { month: "short" }),
      commits: 0,
    }));

    for (const repo of repos) {
      for (let i = 0; i < 12; i++) {
        const since = new Date(year, i, 1).toISOString();
        const until = new Date(year, i + 1, 1).toISOString();

        const res = await fetchWithAuth(
          `https://api.github.com/repos/${username}/${repo.name}/commits?since=${since}&until=${until}`
        );

        if (res.status === 200) {
          const commits = await res.json();
          monthlyCommits[i].commits += commits.length;
        }
      }
    }

    setCommitData(monthlyCommits);
    setShowChart(true);
    setLoading(false);
  };

  const handleSearch = async () => {
    await fetchReposAndYears();
  };

  const handleYearChange = async (e: React.ChangeEvent<HTMLSelectElement>) => {
    const year = parseInt(e.target.value);
    setSelectedYear(year);
    await fetchCommits(year);
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-4 text-center">GitHub Activity Viewer</h1>
      <div className="flex gap-2 mb-6 justify-center">
        <Input
          placeholder="Enter GitHub username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="w-full max-w-md"
        />
        <Button onClick={handleSearch}>Search</Button>
      </div>

      {loading && <p className="text-center">Loading data...</p>}

      {years.length > 0 && (
        <div className="flex justify-center mb-6">
          <select
            className="border border-gray-300 px-4 py-2 rounded-md text-sm"
            value={selectedYear ?? ""}
            onChange={handleYearChange}
          >
            {years.map((year) => (
              <option key={year} value={year}>
                {year}
              </option>
            ))}
          </select>
        </div>
      )}

      {repos.length > 0 && (
        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-2">Repositories</h2>
          <ul className="list-disc ml-6 space-y-1">
            {repos.map((repo) => (
              <li key={repo.id}>
                <a
                  href={repo.html_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:underline"
                >
                  {repo.name}
                </a>
              </li>
            ))}
          </ul>
        </div>
      )}

      {showChart && commitData.length > 0 && (
        <div>
          <h2 className="text-2xl font-semibold mb-2">Commits in {selectedYear}</h2>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={commitData}>
              <XAxis dataKey="month" />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Bar dataKey="commits" fill="#4f46e5" radius={[4, 4, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}
