// src/lib/fetchCommits.ts

export interface CommitData {
    date: string;
    count: number;
  }
  
  export async function fetchAllCommits(username: string, repo: string): Promise<CommitData[]> {
    let page = 1;
    let allCommits: CommitData[] = [];
  
    while (true) {
      const res = await fetch(
        `https://api.github.com/repos/${username}/${repo}/commits?per_page=100&page=${page}`
      );
  
      if (!res.ok) break;
  
      const commits = await res.json();
  
      if (commits.length === 0) break;
  
      const pageData = commits.map((commit: any) => ({
        date: commit.commit.author.date.slice(0, 10),
        count: 1,
      }));
  
      allCommits = allCommits.concat(pageData);
      page++;
    }
  
    return allCommits;
  }
