import { useState, useEffect } from "react";
import UserForm from "./components/UserForm";
import RepoList from "./components/RepoList";
import CommitChart from "@/components/CommitChart";
import { Card, CardContent } from "@/components/ui/card";

interface Repo {
  id: number;
  name: string;
  html_url: string;
  description: string;
}

interface CommitData {
  date: string;
  count: number;
}

function App() {
  const [username, setUsername] = useState("");
  const [repos, setRepos] = useState<Repo[]>([]);
  const [commitData, setCommitData] = useState<CommitData[]>([]);

  const fetchRepos = async (username: string) => {
    const res = await fetch(`https://api.github.com/users/${username}/repos`);
    const data = await res.json();
    setRepos(data);
  };

  const fetchCommits = async (username: string) => {
    const res = await fetch(`https://api.github.com/users/${username}/events`);
    const data = await res.json();

    const pushEvents = data.filter((event: any) => event.type === "PushEvent");

    const commitMap: Record<string, number> = {};

    pushEvents.forEach((event: any) => {
      const date = new Date(event.created_at).toISOString().split("T")[0];
      commitMap[date] = (commitMap[date] || 0) + event.payload.commits.length;
    });

    const chartData: CommitData[] = Object.entries(commitMap).map(
      ([date, count]) => ({ date, count })
    );

    setCommitData(chartData.sort((a, b) => a.date.localeCompare(b.date)));
  };

  const handleSubmit = async (user: string) => {
    setUsername(user);
    await fetchRepos(user);
    await fetchCommits(user);
  };

  return (
    <div className="max-w-4xl mx-auto py-10 px-4">
      <Card>
        <CardContent className="space-y-6">
          <h1 className="text-2xl font-bold text-center">GitHub Activity Viewer</h1>
          <UserForm onSubmit={handleSubmit} />
          {repos.length > 0 && <RepoList repos={repos} />}
          {commitData.length > 0 && <CommitChart data={commitData} />}
        </CardContent>
      </Card>
    </div>
  );
}

export default App;
